define(function() {
    'use strict';

    //jscs:disable maximumLineLength
    /* jshint ignore:start */

    return {
        // source http://www.regular-expressions.info/email.html
        email: /^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?$/i
    };

    /* jshint ignore:end */
    //jscs:enable maximumLineLength
});
